    &lt;style&gt;
    .feed { max-width: 800px; margin: 1rem 0 2rem 0; font-family: system-ui, -apple-system, Segoe UI, Roboto, Ubuntu, Cantarell, "Helvetica Neue", Arial, "Noto Sans", "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol"; }
  .feed-header { display:flex; justify-content:space-between; align-items:center; gap:.75rem; margin:.25rem 0 0.75rem; }
  .feed-link { font-size:.9rem; text-decoration: underline; }
  .feed-items { display:grid; grid-template-columns: 1fr; gap:.75rem; }
  .feed-item { padding: .9rem; border: 1px solid #e5e7eb; border-radius: .75rem; }
    .feed-item h4 { margin:.1rem 0 .3rem; font-size:1.05rem; line-height:1.3; }
    .feed-item time { display:block; font-size:.85rem; opacity:.7; margin-bottom:.35rem; }
    .feed-item p { margin:.25rem 0 0; font-size:.95rem; line-height:1.5; }
    @media (min-width: 720px) {
      .feed-items { grid-template-columns: 1fr 1fr; }
    }
