      &lt;script&gt;
      (async () =&gt; {
        const root = document.querySelector('.feed-items');
        const FEED_PRIMARY = 'https://infomgnt.org/index.xml';
        const FEED_FALLBACK_JSON = 'https://api.rss2json.com/v1/api.json?rss_url=' + encodeURIComponent(FEED_PRIMARY);

        function stripHtml(html) {
          const div = document.createElement('div');
          div.innerHTML = html || '';
          return (div.textContent || div.innerText || '').trim();
        }
        function asDate(d) {
          try { return new Date(d); } catch { return new Date(); }
        }
        function fmtDate(d) {
          return d.toLocaleDateString('de-DE', { year:'numeric', month:'short', day:'2-digit' });
        }
        function itemToHTML({title, link, date, description}) {
          const safeTitle = stripHtml(title || '');
          const safeDesc = stripHtml(description || '').slice(0, 220);
          const dateStr = date ? fmtDate(asDate(date)) : '';
          return `
          &lt;article class="feed-item"&gt;
            &lt;h4&gt;&lt;a href="${link}" target="_blank" rel="noopener"&gt;${safeTitle || 'Ohne Titel'}&lt;/a&gt;&lt;/h4&gt;
              ${dateStr ? `&lt;time datetime="${new Date(date).toISOString()}"&gt;${dateStr}&lt;/time&gt;` : ''}
            ${safeDesc ? `&lt;p&gt;${safeDesc}…&lt;/p&gt;` : ''}
            &lt;/article&gt;
              `;
        }

        async function fetchXML(url) {
          const res = await fetch(url, { headers: { 'Accept': 'application/rss+xml, application/atom+xml, application/xml;q=0.9, text/xml;q=0.8' }});
          if (!res.ok) throw new Error('HTTP ' + res.status);
          const txt = await res.text();
          const doc = new DOMParser().parseFromString(txt, 'application/xml');
          if (doc.querySelector('parsererror')) throw new Error('XML parse error');
          // support RSS &lt;item&gt; and Atom &lt;entry&gt;
            const nodes = [...doc.querySelectorAll('item, entry')].slice(0, 8);
            return nodes.map(n =&gt; {
              const isAtom = n.tagName.toLowerCase() === 'entry';
              const title = (n.querySelector('title')?.textContent || '').trim();
              const linkEl = isAtom ? n.querySelector('link[rel="alternate"]') || n.querySelector('link') : n.querySelector('link');
              const link = isAtom ? (linkEl?.getAttribute('href') || '') : (linkEl?.textContent || '');
              const date = n.querySelector(isAtom ? 'updated, published' : 'pubDate')?.textContent || '';
              const desc = n.querySelector('description, summary, content, content\\:encoded')?.textContent || '';
              return { title, link, date, description: desc };
            });
        }

        async function fetchJSON(url) {
          const res = await fetch(url);
          if (!res.ok) throw new Error('HTTP ' + res.status);
          const data = await res.json();
          const items = (data.items || []).slice(0, 8).map(i =&gt; ({
            title: i.title,
            link: i.link,
            date: i.pubDate || i.updated,
            description: i.description || i.content || i.summary
          }));
          return items;
        }

        try {
          // 1) Versuch: XML direkt (schnell, kein Drittanbieter)
    const items = await fetchXML(FEED_PRIMARY);
    root.innerHTML = items.map(itemToHTML).join('') || '&lt;p&gt;Keine Einträge gefunden.&lt;/p&gt;';
        } catch (e1) {
          // 2) Fallback: RSS→JSON-Service
try { const items = await fetchJSON(FEED_FALLBACK_JSON); root.innerHTML = items.map(itemToHTML).join(’‘) ||’

