‘; } catch (e2) { console.error(’Feed-Fehler’, e1, e2); root.innerHTML = &lt;p&gt;Der Feed konnte nicht geladen werden. &lt;a href="https://infomgnt.org" target="_blank" rel="noopener"&gt;Direkt zu infomgnt.org&lt;/a&gt;&lt;/p&gt;; } } })(); 


